CREATE OR REPLACE FUNCTION obter_saldo_banco_ant_estrang (nr_seq_saldo_banco_p bigint) RETURNS bigint AS $body$
DECLARE


dt_referencia_w		timestamp;
dt_ref_anterior_w	timestamp;
vl_saldo_w		double precision;
nr_seq_conta_w		bigint;


BEGIN

begin
select	dt_referencia,
	nr_seq_conta
into STRICT	dt_referencia_w,
	nr_seq_conta_w
from	banco_saldo
where	nr_sequencia = nr_seq_saldo_banco_p;

select	max(dt_referencia)
into STRICT	dt_ref_anterior_w
from	banco_saldo
where	dt_referencia < dt_referencia_w
and	nr_seq_conta = nr_seq_conta_w;

select	coalesce(max(vl_saldo_estrang), 0)
into STRICT	vl_saldo_w
from	banco_saldo
where	trunc(dt_referencia, 'month') = trunc(dt_ref_anterior_w, 'month')
and	nr_seq_conta = nr_seq_conta_w;
exception
	when others then
		vl_saldo_w := 0;
end;

return vl_saldo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_saldo_banco_ant_estrang (nr_seq_saldo_banco_p bigint) FROM PUBLIC;

