CREATE OR REPLACE FUNCTION obter_saldo_banco_diario_estr ( nr_seq_banco_saldo_p bigint, dt_referencia_p timestamp) RETURNS bigint AS $body$
DECLARE


vl_saldo_atual_w	double precision;
vl_retorno_w		double precision;
vl_transacoes_w		double precision;
nr_seq_conta_w		bigint;
dt_referencia_w		timestamp;
dt_final_w			timestamp;


BEGIN

select	nr_seq_conta
into STRICT	nr_seq_conta_w
from	banco_saldo
where	nr_sequencia	= nr_seq_banco_saldo_p;

select	max(a.dt_referencia)
into STRICT	dt_referencia_w
from	banco_saldo a
where	a.nr_seq_conta	= nr_seq_conta_w;

select	a.vl_saldo_estrang
into STRICT	vl_saldo_atual_w
from	banco_saldo a
where	a.dt_referencia	= dt_referencia_w
and		a.nr_seq_conta	= nr_seq_conta_w;

dt_final_w	:= fim_dia(dt_referencia_p);

select	sum(CASE WHEN b.ie_banco='C' THEN  a.vl_transacao_estrang WHEN b.ie_banco='D' THEN  a.vl_transacao_estrang * -1  ELSE 0 END )
into STRICT	vl_transacoes_w
from	transacao_financeira b,
		banco_saldo c,
		movto_trans_financ a
where	b.ie_banco			<> 'N'
and		a.nr_seq_trans_financ	= b.nr_sequencia
and		c.nr_seq_conta			= nr_seq_conta_w
and		a.nr_seq_saldo_banco	= c.nr_sequencia
and		a.dt_transacao			> dt_final_w;

vl_retorno_w	:= (vl_saldo_atual_w + coalesce(vl_transacoes_w,0));

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
