SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_saldo_banco_extrato (nr_seq_conta_p bigint, dt_inicial_p timestamp, dt_final_p timestamp, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


/*

ie_opcao_p
I - Saldo inicial
F - Saldo Final

*/
vl_lanc_periodo_w		double precision;
dt_final_w				timestamp;
vl_saldo_final_w		double precision;
vl_diferenca_w			double precision;
vl_saldo_W				double precision;
nr_sequencia_w			banco_extrato.nr_sequencia%type;



BEGIN

select	max(dt_final)
into STRICT	dt_final_w
from	banco_extrato
where	(dt_final IS NOT NULL AND dt_final::text <> '')
and	nr_seq_conta	= nr_seq_conta_p;

/*Pegar a maior sequencia lançada para o mesmo dia, pois podem haver mais de um extrato lançado por dia*/

select	max(nr_sequencia)
into STRICT	nr_sequencia_w
from	banco_extrato
where	trunc(dt_final)	= trunc(dt_final_w)
and		nr_seq_conta	= nr_seq_conta_p;

if (nr_sequencia_w IS NOT NULL AND nr_sequencia_w::text <> '') then

	select	coalesce(max(vl_saldo_final),0)
	into STRICT	vl_saldo_final_w
	from	banco_extrato
	where	trunc(dt_final)	= trunc(dt_final_w)
	and		nr_seq_conta	= nr_seq_conta_p
	and		nr_sequencia	= nr_sequencia_w;

else
	vl_saldo_final_w := 0;
end if;

select	coalesce(sum(CASE WHEN a.ie_deb_cred='C' THEN  a.vl_lancamento  ELSE a.vl_lancamento * -1 END ), 0)
into STRICT	vl_diferenca_w
from	banco_extrato b,
		banco_extrato_lanc a
where	a.dt_movimento between	dt_final_p and dt_final_w
and		a.nr_seq_extrato	= b.nr_sequencia
and		b.nr_seq_conta		= nr_seq_conta_p
and		a.ie_deb_cred		in ('D', 'C');


if (ie_opcao_p = 'F') then
	vl_saldo_W	:= vl_saldo_final_w - vl_diferenca_w;
else
	select	coalesce(sum(CASE WHEN a.ie_deb_cred='C' THEN  a.vl_lancamento  ELSE a.vl_lancamento * -1 END ), 0)
	into STRICT	vl_lanc_periodo_w
	from	banco_extrato b,
		banco_extrato_lanc a
	where	a.dt_movimento between	dt_inicial_p and dt_final_p
	and	a.nr_seq_extrato	= b.nr_sequencia
	and	b.nr_seq_conta		= nr_seq_conta_p
	and	a.ie_deb_cred		in ('D', 'C');

	vl_saldo_w	:= vl_saldo_final_w - vl_diferenca_w - vl_lanc_periodo_w;
end if;

return	vl_saldo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
