CREATE OR REPLACE FUNCTION obter_saldo_bruto_cartao_cr (nr_seq_movto_p bigint, dt_referencia_p timestamp) RETURNS bigint AS $body$
DECLARE


vl_transacao_w		double precision;
vl_baixas_w		double precision;
vl_despesa_w		double precision;
vl_saldo_w		double precision;
dt_referencia_w		timestamp;
dt_transacao_w		timestamp;
vl_desp_equip_w		movto_cartao_cr_baixa.vl_desp_equip%type;


BEGIN

if (dt_referencia_p IS NOT NULL AND dt_referencia_p::text <> '') then
	dt_referencia_w	:= trunc(dt_referencia_p,'dd') + 86399/86400;
end if;

select	coalesce(max(vl_transacao),0),
	coalesce(max(dt_transacao),dt_referencia_w)
into STRICT	vl_transacao_w,
	dt_transacao_w
from	movto_cartao_cr
where	nr_sequencia	= nr_seq_movto_p;

if (coalesce(dt_referencia_p::text, '') = '') then
	select	coalesce(sum(vl_baixa),0),
		coalesce(sum(coalesce(vl_despesa,0)),0),
		coalesce(sum(coalesce(vl_desp_equip,0)),0)
	into STRICT	vl_baixas_w,
		vl_despesa_w,
		vl_desp_equip_w
	from	movto_cartao_cr_baixa
	where	nr_seq_movto		= nr_seq_movto_p;
else
	select	coalesce(sum(vl_baixa),0),
		coalesce(sum(coalesce(vl_despesa,0)),0),
		coalesce(sum(coalesce(vl_desp_equip,0)),0)
	into STRICT	vl_baixas_w,
		vl_despesa_w,
		vl_desp_equip_w
	from	movto_cartao_cr_baixa
	where	nr_seq_movto		= nr_seq_movto_p
	and	dt_baixa			<= fim_dia(dt_referencia_w);
end if;

if (coalesce(dt_transacao_w,dt_referencia_w) > dt_referencia_w) then
	vl_saldo_w	:= 0;
else	
	vl_saldo_w	:= vl_transacao_w - vl_baixas_w - vl_despesa_w - vl_desp_equip_w;
end if;

return	coalesce(vl_saldo_w,0);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
