CREATE OR REPLACE FUNCTION obter_saldo_caixas_data (cd_estabelecimento_p bigint, dt_referencia_p timestamp, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE

/* Opcao de saldo
	N - Normal
	V - A vista
*/
vl_saldo_w		double precision	:= null;
vl_saldo_caixa_w	double precision;
vl_saldo_avista_w	double precision;


BEGIN

select	coalesce(sum(CASE WHEN trunc(a.dt_saldo,'dd')=trunc(dt_referencia_p,'dd') THEN a.vl_saldo_inicial  ELSE a.vl_saldo END ),0),
	coalesce(sum(CASE WHEN trunc(a.dt_saldo,'dd')=trunc(dt_referencia_p,'dd') THEN a.vl_saldo_inicial  ELSE coalesce(a.vl_saldo_avista,a.vl_saldo) END ),0)
into STRICT	vl_saldo_caixa_w,
	vl_saldo_avista_w
from	caixa b,
	caixa_saldo_diario a
where	b.CD_ESTABELECIMENTO	= cd_estabelecimento_p
and	a.nr_seq_caixa	= b.nr_sequencia
and	b.ie_situacao	= 'A'
and	b.ie_fluxo 	= 'S'
and	a.dt_saldo = 	(SELECT	max(dt_saldo)
			from	caixa_saldo_diario x
			where	x.nr_seq_caixa	= a.nr_seq_caixa
			and	trunc(x.dt_saldo,'dd') <= trunc(dt_referencia_p,'dd'));

if (ie_opcao_p = 'N') then
	vl_saldo_w	:= vl_saldo_caixa_w;
elsif (ie_opcao_p = 'V') then
	vl_saldo_w	:= vl_saldo_avista_w;

end if;

return	vl_saldo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
