CREATE OR REPLACE FUNCTION obter_saldo_caixa_anterior (nr_seq_saldo_caixa_p bigint) RETURNS bigint AS $body$
DECLARE


vl_saldo_anterior_w	double precision;
dt_saldo_w		timestamp;
nr_seq_caixa_w		bigint;
dt_saldo_anterior_w	timestamp;


BEGIN

begin
select	dt_saldo,
	nr_seq_caixa
into STRICT	dt_saldo_w,
	nr_seq_caixa_w
from	caixa_saldo_diario
where	nr_sequencia	= nr_seq_saldo_caixa_p;

select	max(dt_saldo)
into STRICT	dt_saldo_anterior_w
from	caixa_saldo_diario
where	nr_seq_caixa	= nr_seq_caixa_w
and	dt_saldo	< dt_saldo_w;

select	vl_saldo
into STRICT	vl_saldo_anterior_w
from	caixa_saldo_diario
where	dt_saldo	= dt_saldo_anterior_w
and	nr_seq_caixa	= nr_seq_caixa_w;
exception
	when others then
		vl_saldo_anterior_w	:= 0;
end;

return vl_saldo_anterior_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
