SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_saldo_caixa (nr_seq_caixa_p bigint, dt_referencia_p timestamp) RETURNS bigint AS $body$
DECLARE


vl_saldo_caixa_w	double precision;
dt_referencia_w		timestamp;


BEGIN

if (coalesce(dt_referencia_p::text, '') = '') then
	select	max(dt_saldo)
	into STRICT	dt_referencia_w
	from	caixa_saldo_diario
	where	nr_seq_caixa	= nr_seq_caixa_p;
else
	dt_referencia_w	:= dt_referencia_p;
end if;

select	coalesce(max(a.vl_saldo),0)
into STRICT	vl_saldo_caixa_w
from	caixa_saldo_diario a
where	a.dt_saldo	= dt_referencia_w
and	a.nr_seq_caixa	= nr_seq_caixa_p;

return	vl_saldo_caixa_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
