SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_saldo_conpaci_item_geral (nr_seq_propaci_p bigint, nr_seq_matpaci_p bigint) RETURNS bigint AS $body$
DECLARE


vl_retorno_w		double precision;
vl_lote_auditoria_w	double precision;
vl_item_w		double precision;
vl_saldo_w		double precision;
nr_interno_conta_w	bigint;
qt_glosa_conta_w	bigint;
nr_doc_convenio_w	varchar(20);
ie_acao_glosa_w	motivo_glosa.ie_acao_glosa%type;
vl_reapresentacao_w	double precision;
vl_pago_w	double precision;
vl_glosa_aceita_w double precision;


BEGIN

if (nr_seq_propaci_p IS NOT NULL AND nr_seq_propaci_p::text <> '') then
	select	max(a.vl_procedimento),
			max(a.nr_interno_conta),
			max(a.nr_doc_convenio)
	into STRICT	vl_item_w,
			nr_interno_conta_w,
			nr_doc_convenio_w
	from	procedimento_paciente a
	where	a.nr_sequencia		= nr_seq_propaci_p;

	select	count(*)
	into STRICT	qt_glosa_conta_w
	from	convenio_retorno b,
			convenio_retorno_item a
	where	not exists (SELECT	1
		from	convenio_retorno x
		where	x.nr_seq_ret_estorno	= b.nr_sequencia)
	and	coalesce(b.nr_seq_ret_estorno::text, '') = ''
	and	b.ie_status_retorno	= 'F'
	and	a.nr_seq_retorno	= b.nr_sequencia
	and	not exists (select	1
		from	convenio_retorno_glosa x
		where	x.nr_seq_propaci	= nr_seq_propaci_p
		and	x.nr_seq_ret_item	= a.nr_sequencia)
	and	a.vl_amenor		<= 0
	and	a.vl_pago		> 0
	and	coalesce(a.cd_autorizacao, Wheb_mensagem_pck.get_Texto(306761))	= coalesce(nr_doc_convenio_w, Wheb_mensagem_pck.get_Texto(306761))
  	and	a.nr_interno_conta	= nr_interno_conta_w;

	/* ahoffelder - OS 346392 - 25/08/2011 - se existir um retorno com valor pago onde o item não está,
 significa que ele já foi pago */
	if (qt_glosa_conta_w > 0) then

		vl_saldo_w	:= 0;

	/* senão, é necessário consultar os valores do item no retorno e na GRG */

	else
		select	max(b.ie_acao_glosa)
		into STRICT	ie_acao_glosa_w
		from	motivo_glosa b,
				convenio_retorno_Glosa a
		where	a.cd_motivo_glosa = b.cd_motivo_glosa
		and		a.nr_seq_propaci = nr_seq_propaci_p;

		--select	nvl(sum(decode(nvl(a.ie_acao_glosa,b.ie_acao_glosa),'A',a.vl_glosa,0)),0) --aceita
		select	coalesce(sum(CASE WHEN b.ie_acao_glosa='A' THEN a.vl_glosa  ELSE 0 END ),0) --aceita
		into STRICT	vl_glosa_aceita_w
		from	motivo_glosa b,
				convenio_retorno_glosa a
		where	a.nr_seq_propaci	= nr_seq_propaci_p
		and		a.cd_motivo_glosa	= b.cd_motivo_glosa;

		select	coalesce(sum(CASE WHEN b.ie_acao_glosa='R' THEN a.vl_glosa  ELSE 0 END ),0) --reapresentacao
		into STRICT	vl_reapresentacao_w
		from	motivo_glosa b,
				convenio_retorno_glosa a
		where	a.nr_seq_propaci	= nr_seq_propaci_p
		and		a.cd_motivo_glosa	= b.cd_motivo_glosa;

		select	coalesce(sum(a.vl_glosa),0)
		into STRICT	vl_lote_auditoria_w
		from	lote_audit_hist_item a
		where	a.nr_seq_propaci	= nr_seq_propaci_p;

		vl_pago_w 	:= coalesce(vl_item_w,0) - coalesce(vl_glosa_aceita_w,0) - coalesce(vl_reapresentacao_w,0);

		vl_saldo_w 	:= coalesce(vl_item_w,0) - coalesce(vl_pago_w,0) - coalesce(vl_lote_auditoria_w,0);
	end if;

elsif (nr_seq_matpaci_p IS NOT NULL AND nr_seq_matpaci_p::text <> '') then

	select	max(a.vl_material),
			max(a.nr_interno_conta),
			max(a.nr_doc_convenio)
	into STRICT	vl_item_w,
			nr_interno_conta_w,
			nr_doc_convenio_w
	from	material_atend_paciente a
	where	a.nr_sequencia		= nr_seq_matpaci_p;

	select	count(*)
	into STRICT	qt_glosa_conta_w
	from	convenio_retorno b,
			convenio_retorno_item a
	where	not exists (SELECT	1
		from	convenio_retorno x
		where	x.nr_seq_ret_estorno	= b.nr_sequencia)
	and	coalesce(b.nr_seq_ret_estorno::text, '') = ''
	and	b.ie_status_retorno	= 'F'
	and	a.nr_seq_retorno	= b.nr_sequencia
	and	not exists (select	1
		from	convenio_retorno_glosa x
		where	x.nr_seq_matpaci	= nr_seq_matpaci_p
	and	x.nr_seq_ret_item	= a.nr_sequencia)
	and	a.vl_amenor		<= 0
	and	a.vl_pago		> 0
	and	coalesce(a.cd_autorizacao, Wheb_mensagem_pck.get_Texto(306761))	= coalesce(nr_doc_convenio_w, Wheb_mensagem_pck.get_Texto(306761))
	and	a.nr_interno_conta	= nr_interno_conta_w;

	/* ahoffelder - OS 346392 - 25/08/2011 - se existir um retorno com valor pago onde o item não está,
 significa que ele já foi pago */
	if (qt_glosa_conta_w > 0) then
		vl_saldo_w	:= 0;

	/* senãoo, é necessário consultar os valores do item no retorno e na GRG */

	else
		select	coalesce(sum(CASE WHEN coalesce(a.ie_acao_glosa,b.ie_acao_glosa)='A' THEN a.vl_glosa  ELSE 0 END ),0) + coalesce(sum((obter_dados_ret_movto_glosa(a.nr_sequencia, 3))::numeric ),0)
		into STRICT	vl_retorno_w
		from	motivo_glosa b,
				convenio_retorno_glosa a
		where	a.nr_seq_matpaci		= nr_seq_matpaci_p
		and		a.cd_motivo_glosa		= b.cd_motivo_glosa;

		select	coalesce(sum(a.vl_glosa),0) + coalesce(sum(a.vl_pago),0)
		into STRICT	vl_lote_auditoria_w
		from	lote_audit_hist_item a
		where	a.nr_seq_matpaci	= nr_seq_matpaci_p;

		vl_saldo_w	:= coalesce(vl_item_w,0) - coalesce(vl_retorno_w,0) - coalesce(vl_lote_auditoria_w,0);

	end if;

end if;

RETURN	vl_saldo_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_saldo_conpaci_item_geral (nr_seq_propaci_p bigint, nr_seq_matpaci_p bigint) FROM PUBLIC;

