CREATE OR REPLACE FUNCTION obter_saldo_consig_lote ( cd_estabelecimento_p bigint, cd_material_p bigint, cd_local_estoque_p bigint, cd_cgc_fornec_p text, dt_mesano_referencia_p timestamp, nr_seq_lote_p bigint) RETURNS bigint AS $body$
DECLARE


cd_material_w		integer;
qt_estoque_w		double precision;


BEGIN
select	coalesce(cd_material_estoque, cd_material)
into STRICT	cd_material_w
from	material
where	cd_material = cd_material_p;

select	coalesce(sum(qt_estoque),0)
into STRICT	qt_estoque_w
from	fornecedor_mat_consig_lote
where	cd_estabelecimento	= cd_estabelecimento_p
and	cd_local_estoque	= cd_local_estoque_p
and	cd_material		= cd_material_w
and	dt_mesano_referencia	= dt_mesano_referencia_p
and	cd_fornecedor		= cd_cgc_fornec_p
and	nr_seq_lote		= nr_seq_lote_p;

return qt_estoque_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
