CREATE OR REPLACE FUNCTION obter_saldo_conta_banco_data (nr_seq_conta_p bigint, dt_referencia_p timestamp) RETURNS bigint AS $body$
DECLARE


vl_saldo_w		double precision	:= null;
nr_seq_banco_saldo_w	bigint;
dt_referencia_w		timestamp;


BEGIN

select	max(x.dt_referencia)
into STRICT	dt_referencia_w
from	banco_saldo x
where	x.dt_referencia	<= dt_referencia_p
and	x.nr_seq_conta	= nr_seq_conta_p;

select	max(a.nr_sequencia)
into STRICT	nr_seq_banco_saldo_w
from	banco_saldo a
where	a.nr_seq_conta	= nr_seq_conta_p
and	a.dt_referencia	= dt_referencia_w;

if (nr_seq_banco_saldo_w IS NOT NULL AND nr_seq_banco_saldo_w::text <> '') then

	select	max(obter_saldo_banco_diario(nr_seq_banco_saldo_w,dt_referencia_p))
	into STRICT	vl_saldo_w
	;

end if;

return	vl_saldo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
