CREATE OR REPLACE FUNCTION obter_saldo_conta_concil (nr_seq_conta_banco_p bigint, dt_referencia_p timestamp, ie_concil_p text) RETURNS bigint AS $body$
DECLARE


vl_retorno_w		double precision;

/*
ie_concil_p
N - Não conciliado atual
C - Conciliado atual
ND - Não conciliado data
CD - Conciliado data
*/
BEGIN

if (ie_concil_p in ('S','N','T')) then

	select	coalesce(sum(CASE WHEN b.ie_banco='C' THEN  a.vl_transacao * -1 WHEN b.ie_banco='D' THEN  a.vl_transacao  ELSE 0 END ),0)
	into STRICT	vl_retorno_w
	from	transacao_financeira b,
		banco_saldo c,
		movto_trans_financ a
	where	a.nr_seq_trans_financ		= b.nr_sequencia
	and	b.ie_banco			<> 'N'
	and	a.nr_seq_saldo_banco		= c.nr_sequencia
	and	c.nr_seq_conta			= nr_seq_conta_banco_p
	and	trunc(a.dt_transacao, 'dd')	<= trunc(dt_referencia_p, 'dd')
	and	((((a.nr_seq_concil IS NOT NULL AND a.nr_seq_concil::text <> '') or ie_conciliacao = 'S') and ie_concil_p = 'S') or
		 ((coalesce(a.nr_seq_concil::text, '') = '' and ie_conciliacao = 'N') and ie_concil_p = 'N') or
		 ie_concil_p = 'T');

else
	select	coalesce(sum(CASE WHEN b.ie_banco='C' THEN  a.vl_transacao * -1 WHEN b.ie_banco='D' THEN  a.vl_transacao  ELSE 0 END ),0)
	into STRICT	vl_retorno_w
	from	transacao_financeira b,
		banco_saldo c,
		movto_trans_financ a
	where	a.nr_seq_trans_financ		= b.nr_sequencia
	and	b.ie_banco			<> 'N'
	and	a.nr_seq_saldo_banco		= c.nr_sequencia
	and	c.nr_seq_conta			= nr_seq_conta_banco_p
	and	trunc(a.dt_transacao, 'dd')	<= trunc(dt_referencia_p, 'dd')
	and	((ie_concil_p = 'ND' and obter_se_movto_concil(a.nr_sequencia,dt_referencia_p) = 'N') or (ie_concil_p = 'SD' and obter_se_movto_concil(a.nr_sequencia,dt_referencia_p) = 'S'));
end if;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
