SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_saldo_contrato_oc ( nr_seq_contrato_p bigint) RETURNS bigint AS $body$
DECLARE


vl_total_contrato_w		double precision;
vl_total_ordem_w		double precision;
vl_saldo_w		double precision;


BEGIN
vl_saldo_w	:= 0;

select	coalesce(vl_total_contrato,0)
into STRICT	vl_total_contrato_w
from	contrato
where	nr_sequencia = nr_seq_contrato_p;

select	coalesce(sum(obter_valor_liquido_ordem(nr_ordem_compra)),0)
into STRICT	vl_total_ordem_w
from	ordem_compra
where	nr_seq_contrato = nr_seq_contrato_p
and (coalesce(nr_seq_motivo_cancel::text, '') = '')
and (obter_se_ordem_reprovada(nr_ordem_compra) = 'N')
and (dt_liberacao IS NOT NULL AND dt_liberacao::text <> '');

vl_saldo_w := vl_total_contrato_w - vl_total_ordem_w;

return	vl_saldo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_saldo_contrato_oc ( nr_seq_contrato_p bigint) FROM PUBLIC;

