CREATE OR REPLACE FUNCTION obter_saldo_cont_inicial_lote ( cd_estabelecimento_p bigint, cd_material_p bigint, cd_local_estoque_p bigint, dt_mesano_referencia_p timestamp, cd_fornecedor_p text, ie_consignado_p text) RETURNS bigint AS $body$
DECLARE


qt_saldo_disp_estoque_w	double precision;
qt_contagem_w		double precision;
qt_saldo_w		double precision;

BEGIN
if (ie_consignado_p = 'S') then
	qt_saldo_disp_estoque_w := obter_saldo_estoque_consig(cd_estabelecimento_p, cd_fornecedor_p, cd_material_p, cd_local_estoque_p);
else
	qt_saldo_disp_estoque_w := obter_saldo_disp_estoque(cd_estabelecimento_p, cd_material_p, cd_local_estoque_p, dt_mesano_referencia_p);
end if;

select	coalesce(sum(qt_material),0)
into STRICT	qt_contagem_w
from	w_contagem_inicial_lote
where	cd_local_estoque 	= cd_local_estoque_p
and	cd_material 	= cd_material_p
and	coalesce(cd_fornecedor,'0') = coalesce(cd_fornecedor_p,'0');

qt_saldo_w := qt_saldo_disp_estoque_w - qt_contagem_w;

return	qt_saldo_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
