SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_saldo_data_tit_rec ( nr_titulo_p bigint, dt_referencia_p timestamp) RETURNS bigint AS $body$
DECLARE

 
dt_referencia_w	timestamp;
dt_contabil_w		timestamp;
ie_situacao_w		varchar(1);
vl_titulo_w		double precision;
vl_saldo_Data_w	double precision;
vl_recebido_w		double precision;
vl_alteracao_w	double precision;
vl_desdobrado_w	double precision;
vl_tributo_w	double precision;


BEGIN 
 
dt_referencia_w	:= Trunc(dt_referencia_p,'dd') + 86399/86400;
 
begin 
select	vl_titulo, 
	dt_contabil, 
	ie_situacao 
into STRICT	vl_titulo_w, 
	dt_contabil_w, 
	ie_situacao_w 
from	Titulo_receber 
where	nr_titulo	= nr_titulo_p;
exception 
  		when others then 
		vl_titulo_w	:= 0;
end;
 
select	coalesce(sum(CASE WHEN ie_aumenta_diminui='A' THEN  VL_ALTERACAO  ELSE VL_ALTERACAO * -1 END ),0) 
into STRICT	vl_alteracao_w 
from	ALTERACAO_VALOR 
where	nr_titulo	= nr_titulo_p 
and	DT_ALTERACAO	<= dt_referencia_w;
 
if (ie_situacao_w = '5') then 
	select coalesce(sum(coalesce(vl_recebido,0) + coalesce(vl_glosa,0) + coalesce(vl_descontos,0) + coalesce(vl_perdas,0) + coalesce(vl_nota_credito,0) + coalesce(vl_cambial_passivo,0)),0) 
	into STRICT	vl_recebido_w 
	from 	titulo_receber_liq 
	where	nr_titulo		= nr_titulo_p 
	and	dt_recebimento	> dt_referencia_w 
	and	coalesce(ie_lib_caixa, 'S') = 'S';
 
	select coalesce(sum(a.vl_titulo),0) 
	into STRICT	vl_desdobrado_w 
	from 	Titulo_receber b, 
			titulo_receber_desdob a 
	where	a.nr_titulo		= nr_titulo_p 
	and	a.nr_titulo_dest	= b.nr_titulo;
	/*and	to_char(b.dt_contabil,'dd/mm/yyyy')		>= to_char(dt_referencia_w,'dd/mm/yyyy'); /*aamfirmo OS 814969 Coloquei no lugar de apenas > para >= e tive que desconsidera as horas, e considera apenas a data pois 
													a data contabil do titulo estava sem horas minutos e segundos e a data de referencia possuia horas minutos e segundos., onde nunca era encontrado 
													esse valor, sempre ocsaionando diferença, pois esse valor desdobrado nao era achado e causava diferença no valor final do relatorio da função titulo receb*/
 
 
	vl_saldo_data_w		:= vl_recebido_w + vl_desdobrado_w;
else 
	select coalesce(sum(coalesce(vl_recebido,0) + coalesce(vl_glosa,0) + coalesce(vl_descontos,0) + coalesce(vl_perdas,0) + coalesce(vl_nota_credito,0) + coalesce(vl_cambial_passivo,0)),0) 
	into STRICT	vl_recebido_w 
	from 	titulo_receber_liq 
	where	nr_titulo		= nr_titulo_p 
	and	dt_recebimento	<= dt_referencia_w 
	and	coalesce(ie_lib_caixa, 'S') = 'S';
 
	vl_saldo_data_w		:= vl_titulo_w - vl_recebido_w;
 
end if;
 
select	coalesce(sum(VL_TRIBUTO),0) 
into STRICT	vl_tributo_w 
from	titulo_receber_trib 
where	nr_titulo		= nr_titulo_p 
and	coalesce(ie_origem_tributo, 'C') in ('D','CD');
 
vl_Saldo_Data_w	:= vl_Saldo_Data_w + vl_alteracao_w - vl_tributo_w;
 
RETURN coalesce(vl_Saldo_Data_w,0);
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
