CREATE OR REPLACE FUNCTION obter_saldo_despesa_cartao_cr (nr_seq_movto_p bigint, nr_seq_parcela_p bigint, dt_referencia_p timestamp) RETURNS bigint AS $body$
DECLARE


vl_despesa_w			double precision;
vl_baixas_w			double precision;
vl_saldo_w			double precision;
dt_referencia_w			timestamp;
ie_zerar_canc_cartao_cr_w	varchar(1);
cd_estabelecimento_w		smallint;
dt_cancelamento_w		timestamp;


BEGIN

if (dt_referencia_p IS NOT NULL AND dt_referencia_p::text <> '') then
	dt_referencia_w	:= trunc(dt_referencia_p,'dd') + 86399/86400;
end if;

if (nr_seq_movto_p IS NOT NULL AND nr_seq_movto_p::text <> '') then

	select	max(a.cd_estabelecimento),
		max(a.dt_cancelamento)
	into STRICT	cd_estabelecimento_w,
		dt_cancelamento_w
	from	movto_cartao_cr a
	where	a.nr_sequencia		= nr_seq_movto_p;

	select	max(a.ie_zerar_canc_cartao_cr)
	into STRICT	ie_zerar_canc_cartao_cr_w
	from	parametro_contas_receber a
	where	a.cd_estabelecimento	= cd_estabelecimento_w;

	select	coalesce(sum(vl_despesa),0)
	into STRICT	vl_despesa_w
	from	movto_cartao_cr_parcela
	where	nr_seq_movto	= nr_seq_movto_p;

	if (coalesce(dt_referencia_p::text, '') = '') then
		select	coalesce(sum(vl_despesa),0)
		into STRICT	vl_baixas_w
		from	movto_cartao_cr_baixa
		where	nr_seq_movto		= nr_seq_movto_p;
	else
		select	coalesce(sum(vl_despesa),0)
		into STRICT	vl_baixas_w
		from	movto_cartao_cr_baixa
		where	nr_seq_movto		= nr_seq_movto_p
		and	dt_baixa		<= fim_dia(dt_referencia_w);
	end if;

elsif (nr_seq_parcela_p IS NOT NULL AND nr_seq_parcela_p::text <> '') then

	select	coalesce(vl_despesa,0)
	into STRICT	vl_despesa_w
	from	movto_cartao_cr_parcela
	where	nr_sequencia	 = nr_seq_parcela_p;

	if (coalesce(dt_referencia_p::text, '') = '') then
		select	coalesce(sum(vl_despesa),0)
		into STRICT	vl_baixas_w
		from	movto_cartao_cr_baixa
		where	nr_seq_parcela		= nr_seq_parcela_p;
	else
		select	coalesce(sum(vl_despesa),0)
		into STRICT	vl_baixas_w
		from	movto_cartao_cr_baixa
		where	nr_seq_parcela		= nr_seq_parcela_p
		and	dt_baixa		<= fim_dia(dt_referencia_w);
	end if;
end if;

if (nr_seq_movto_p IS NOT NULL AND nr_seq_movto_p::text <> '') and (ie_zerar_canc_cartao_cr_w in ('S','A')) and (dt_cancelamento_w <= coalesce(dt_referencia_w,clock_timestamp())) then
	vl_saldo_w	:= 0;
else
	vl_saldo_w	:= coalesce(vl_despesa_w - vl_baixas_w,0);
end if;

return	vl_saldo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
