CREATE OR REPLACE FUNCTION obter_saldo_disp_est ( cd_estabelecimento_p bigint, cd_material_p bigint, cd_local_estoque_p bigint, dt_mesano_referencia_p timestamp) RETURNS bigint AS $body$
DECLARE


