CREATE OR REPLACE FUNCTION obter_saldo_emprestimo_expurgo ( nr_seq_conjunto_p bigint, cd_local_estoque_p bigint) RETURNS bigint AS $body$
DECLARE


qt_saldo_w		cm_expurgo_saldo.qt_saldo%type;


BEGIN

select	coalesce(max(qt_saldo),0)
into STRICT	qt_saldo_w
from	cm_expurgo_saldo
where	nr_seq_conjunto = nr_seq_conjunto_p
and	cd_local_estoque = cd_local_estoque_p;

if (qt_saldo_w >= 0) then

	qt_saldo_w := 0;
	
elsif (qt_saldo_w < 0) then

	qt_saldo_w := (qt_saldo_w * -1);

end if;

return	qt_saldo_w;

end;
$body$
LANGUAGE PLPGSQL
