CREATE OR REPLACE FUNCTION obter_saldo_estoque_consig ( cd_estabelecimento_p bigint, cd_cgc_fornec_p text, cd_material_estoque_p bigint, cd_local_estoque_p bigint) RETURNS bigint AS $body$
DECLARE


qt_saldo_estoque_w		double precision	:= 0;
dt_mesano_referencia_w		timestamp;
cd_material_estoque_w		integer;


BEGIN

select	max(dt_mesano_vigente)
into STRICT 	dt_mesano_referencia_w
from 	parametro_estoque
where 	cd_estabelecimento	= cd_estabelecimento_p;

select	max(cd_material_estoque)
into STRICT	cd_material_estoque_w
from	material
where	cd_material	= cd_material_estoque_p;


select	coalesce(sum(qt_estoque), 0)
into STRICT	qt_saldo_estoque_w
from	(
	SELECT	coalesce(sum(a.qt_estoque),0) qt_estoque
	from	fornecedor_mat_consignado a
	where	a.cd_estabelecimento	= cd_estabelecimento_p
	and	a.cd_local_estoque		= cd_local_estoque_p
	and	a.cd_fornecedor		= coalesce(cd_cgc_fornec_p, a.cd_fornecedor)
	and	a.cd_material		= cd_material_estoque_w
	and	coalesce(cd_local_estoque_p,0) > 0
	and	a.dt_mesano_referencia	= (
		SELECT	max(x.dt_mesano_referencia)
		from	fornecedor_mat_consignado x
		where	x.cd_estabelecimento	= a.cd_estabelecimento
		and	x.cd_local_estoque		= a.cd_local_estoque
		and	x.cd_fornecedor		= a.cd_fornecedor
		and	x.cd_material		= a.cd_material
		and	x.dt_mesano_referencia	>= dt_mesano_referencia_w)
	
union all

	select	coalesce(sum(a.qt_estoque),0) qt_estoque
	from	fornecedor_mat_consignado a
	where	a.cd_estabelecimento	= cd_estabelecimento_p
	and	a.cd_fornecedor		= coalesce(cd_cgc_fornec_p, a.cd_fornecedor)
	and	a.cd_material		= cd_material_estoque_w
	and	coalesce(cd_local_estoque_p,0) = 0
	and	a.dt_mesano_referencia	= (
		select	max(x.dt_mesano_referencia)
		from	fornecedor_mat_consignado x
		where	x.cd_estabelecimento	= a.cd_estabelecimento
		and	x.cd_local_estoque		= a.cd_local_estoque
		and	x.cd_fornecedor		= a.cd_fornecedor
		and	x.cd_material		= a.cd_material
		and	x.dt_mesano_referencia	>= dt_mesano_referencia_w)) alias14;


return qt_saldo_estoque_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
