CREATE OR REPLACE FUNCTION obter_saldo_estoque_req_palm ( nr_requisicao_p bigint, nr_sequencia_p bigint, nr_seq_lote_fornec_p bigint) RETURNS bigint AS $body$
DECLARE


cd_estabelecimento_w		estabelecimento.cd_estabelecimento%type;
cd_material_w				material.cd_material%type;
cd_local_estoque_w			local_estoque.cd_local_estoque%type;
qt_estoque_w				double precision := 0;
cd_unidade_medida_w			item_requisicao_material.cd_unidade_medida%type;
cd_unidade_medida_estoque_w	item_requisicao_material.cd_unidade_medida_estoque%type;
qt_conv_estoque_consumo_w	material.qt_conv_estoque_consumo%type;


BEGIN

select	a.cd_estabelecimento,
	a.cd_local_estoque,
	b.cd_material,
	b.cd_unidade_medida,
	b.cd_unidade_medida_estoque
into STRICT	cd_estabelecimento_w,
	cd_local_estoque_w,
	cd_material_w,
	cd_unidade_medida_w,
	cd_unidade_medida_estoque_w
from	requisicao_material a,
	item_requisicao_material b
where	a.nr_requisicao = b.nr_requisicao
and	b.nr_sequencia = nr_sequencia_p
and	b.nr_requisicao = nr_requisicao_p;

qt_estoque_w := obter_saldo_total_disp_estoque(
					cd_material_p		=> cd_material_w,
					cd_local_estoque_p	=> cd_local_estoque_w,
					cd_estabelecimento_p	=> cd_estabelecimento_w,
					nr_seq_lote_fornec_p	=> nr_seq_lote_fornec_p);
					
if (cd_unidade_medida_w <> cd_unidade_medida_estoque_w) then
	qt_conv_estoque_consumo_w := obter_conversao_material(cd_material_w, 'EC');
	qt_estoque_w := qt_estoque_w * coalesce(qt_conv_estoque_consumo_w,1);
end if;

return qt_estoque_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
