CREATE OR REPLACE FUNCTION obter_saldo_estoque_req ( cd_estabelecimento_p bigint, cd_material_p bigint, cd_local_estoque_p bigint, cd_fornecedor_p text, dt_mesano_referencia_p timestamp, ie_consignado_p text, ie_generico_p text) RETURNS bigint AS $body$
DECLARE


qt_estoque_w		double precision := 0;
qt_estoque_ww		double precision := 0;

cd_material_estoque_w	material.cd_material%type;
cd_material_generico_w	material.cd_material%type;

cd_material_w		material.cd_material%type;

C01 CURSOR FOR
SELECT	a.cd_material
from	material a
where	a.cd_material_generico = cd_material_p
and	a.cd_material_estoque <> cd_material_estoque_w
and	coalesce(ie_generico_p,'N') = 'S'
and	1 = 2 /*Não considerar o saldo dos comerciais do genérico, de acordo com o histórico da OS 640069 em 23/10/2013 15:42:03*/
union

select	a.cd_material
from	material a
where	a.cd_material = cd_material_generico_w
and	a.cd_material_estoque <> cd_material_estoque_w
and	coalesce(ie_generico_p,'N') = 'S' /*OS 640069*/
union

select	a.cd_material
from	material a
where	a.cd_material = cd_material_p;


BEGIN
begin
select	cd_material_estoque,
	cd_material_generico
into STRICT	cd_material_estoque_w,
	cd_material_generico_w
from	material
where	cd_material	= cd_material_p;
exception
when others then
	cd_material_estoque_w := null;
end;

open C01;
loop
fetch C01 into
	cd_material_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (ie_consignado_p = 'N') then
		qt_estoque_ww := obter_saldo_estoque(cd_estabelecimento_p, cd_material_w, cd_local_estoque_p, dt_mesano_referencia_p, qt_estoque_ww);
	elsif (ie_consignado_p = 'S') then
		qt_estoque_ww := obter_saldo_estoque_consig(cd_estabelecimento_p, cd_fornecedor_p, cd_material_w, cd_local_estoque_p);
	end if;

	qt_estoque_w := qt_estoque_w + coalesce(qt_estoque_ww,0);
	end;
end loop;
close C01;

return qt_estoque_w;

end;
