CREATE OR REPLACE FUNCTION obter_saldo_guia (nr_seq_ret_item_p bigint) RETURNS bigint AS $body$
DECLARE


vl_retorno_w	 	double precision := 0;
vl_ret_glosa_w		double precision;
vl_glosa_guia_w		double precision;
vl_adicional_w		double precision;
vl_amaior_glosa_w	double precision;

BEGIN

begin
select  coalesce(sum(b.vl_glosa),0),
	coalesce(sum(b.vl_amaior),0)
into STRICT	vl_ret_glosa_w,
	vl_amaior_glosa_w
from	convenio_retorno_glosa b
where	b.nr_seq_ret_item	= nr_seq_ret_item_p;
exception
when others then
	vl_ret_glosa_w 	  := 0;
	vl_amaior_glosa_w := 0;
end;

begin
select  coalesce(coalesce(a.vl_glosado,0) + coalesce(a.vl_amenor,0),0),
	coalesce(vl_adicional,0)
into STRICT	vl_glosa_guia_w,
	vl_adicional_w
from	convenio_retorno_item a
where	a.nr_sequencia = nr_seq_ret_item_p;
exception
when others then
	vl_glosa_guia_w := 0;
	vl_adicional_w	:= 0;
end;

if (coalesce(vl_glosa_guia_w,0) <> coalesce(vl_ret_glosa_w,0)) then

	if (vl_glosa_guia_w > vl_ret_glosa_w) then
		vl_retorno_w := vl_glosa_guia_w - vl_ret_glosa_w;
	elsif (vl_ret_glosa_w >  vl_glosa_guia_w) then
		vl_retorno_w := vl_ret_glosa_w - vl_glosa_guia_w;
	end if;

elsif (coalesce(vl_adicional_w,0) <> coalesce(vl_amaior_glosa_w,0)) then

	if (vl_adicional_w > vl_amaior_glosa_w) then
		vl_retorno_w := vl_adicional_w - vl_amaior_glosa_w;
	elsif (vl_amaior_glosa_w >  vl_adicional_w) then
		vl_retorno_w := vl_amaior_glosa_w - vl_adicional_w;
	end if;

end if;


return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
