CREATE OR REPLACE FUNCTION obter_saldo_inicial_caixa (nr_seq_saldo_caixa_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


/*
V - Saldo a vista

*/
vl_retorno_w		double precision;
vl_saldo_avista_w	double precision;
dt_inicio_w		timestamp;
nr_seq_caixa_w		bigint;


BEGIN

select	max(dt_saldo),
	max(nr_seq_caixa)
into STRICT	dt_inicio_w,
	nr_seq_caixa_w
from	caixa_saldo_diario
where	nr_sequencia	= nr_seq_saldo_caixa_p;

select	coalesce(max(a.vl_saldo_avista),0)
into STRICT	vl_saldo_avista_w
from	caixa_saldo_diario a
where	a.nr_seq_caixa	= nr_seq_caixa_w
and	a.dt_saldo = 	(SELECT	max(dt_saldo)
			from	caixa_saldo_diario x
			where	x.nr_seq_caixa	= a.nr_seq_caixa
			and	trunc(x.dt_saldo,'dd') < trunc(dt_inicio_w,'dd'));

if (ie_opcao_p	= 'V') then
	vl_retorno_w	:= vl_saldo_avista_w;
end if;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
