CREATE OR REPLACE FUNCTION obter_saldo_inicial_fluxo ( cd_estabelecimento_p bigint, dt_referencia_p timestamp, cd_empresa_p bigint) RETURNS bigint AS $body$
DECLARE


vl_inicial_w			double precision;


BEGIN

select	sum(coalesce(vl_saldo,0))
into STRICT	vl_inicial_w
from	banco_saldo b,
	banco_estabelecimento a
where	a.cd_estabelecimento	= cd_estabelecimento_p
and	a.ie_fluxo_caixa	= 'S'
and	a.nr_sequencia	= b.nr_seq_conta
and	b.dt_referencia	= trunc(dt_referencia_p,'month');

return coalesce(vl_inicial_w,0);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
