CREATE OR REPLACE FUNCTION obter_saldo_local_solic ( nr_ordem_compra_p bigint, cd_material_p bigint, cd_local_estoque_p bigint ) RETURNS bigint AS $body$
DECLARE


saldo_solic_w  integer	:= 0;


BEGIN

    begin
        select  obter_saldo_disp_estoque(cd_estabelecimento, cd_material_p, cd_local_estoque_p, trunc(clock_timestamp(),'mm'))
        into STRICT    saldo_solic_w
        from    ordem_compra
        where   nr_ordem_compra = nr_ordem_compra_p;
    exception
        when no_data_found then return null;
        when too_many_rows then
            saldo_solic_w := 0;
    end;

return saldo_solic_w;

end;
$body$
