CREATE OR REPLACE FUNCTION obter_saldo_lotes_inspecao (nr_sequencia_p bigint) RETURNS bigint AS $body$
DECLARE


qt_saldo_w		double precision;
qt_lote_w		double precision;
qt_inspecao_w		double precision;


BEGIN

select	obter_qt_convert_item_inspecao(nr_sequencia_p)
into STRICT	qt_inspecao_w
;

select	coalesce(sum(qt_material),0)
into STRICT	qt_lote_w
from	inspecao_recebimento_lote
where	nr_seq_inspecao = nr_sequencia_p;

qt_saldo_w := coalesce(qt_inspecao_w - qt_lote_w,0);

return	qt_saldo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_saldo_lotes_inspecao (nr_sequencia_p bigint) FROM PUBLIC;

