CREATE OR REPLACE FUNCTION obter_saldo_lote_fornecedor ( cd_estabelecimento_p bigint, cd_local_estoque_p bigint, dt_mesano_referencia_p timestamp, nr_seq_lote_p bigint) RETURNS bigint AS $body$
DECLARE


dt_mesano_referencia_w	timestamp;
cd_material_w		integer;
ie_estoque_lote_w		varchar(1);
qt_saldo_w		double precision;


BEGIN

dt_mesano_referencia_w := trunc(coalesce(dt_mesano_referencia_p,clock_timestamp()),'mm');

select	coalesce(b.cd_material_estoque,b.cd_material)
into STRICT	cd_material_w
from	material_lote_fornec a,
	material b
where	a.cd_material = b.cd_material
and	a.nr_sequencia = nr_seq_lote_p;

select	coalesce(max(ie_estoque_lote),'N')
into STRICT	ie_estoque_lote_w
from	material_estab
where	cd_material = cd_material_w
and	cd_estabelecimento = cd_estabelecimento_p;

if (ie_estoque_lote_w = 'S') then
	begin
	qt_saldo_w := obter_saldo_estoque_lote(	cd_estabelecimento_p,
						cd_material_w,
						cd_local_estoque_p,
						dt_mesano_referencia_w,
						nr_seq_lote_p);
	end;
else
	begin
	select	coalesce(sum(CASE WHEN 	c.ie_entrada_saida='E' THEN 	CASE WHEN b.cd_acao=1 THEN  b.qt_estoque  ELSE b.qt_estoque * -1 END   ELSE CASE WHEN b.cd_acao=1 THEN  b.qt_estoque * -1  ELSE b.qt_estoque END  END ),0)
	into STRICT	qt_saldo_w
	from	material_lote_fornec a,
		movimento_estoque b,
		operacao_estoque c
	where	a.nr_sequencia 		= b.nr_seq_lote_fornec
	and	b.cd_operacao_estoque 	= c.cd_operacao_estoque
	and	(b.dt_processo IS NOT NULL AND b.dt_processo::text <> '')
	and	a.cd_estabelecimento 	= b.cd_estabelecimento
	and	a.cd_estabelecimento 	= cd_estabelecimento_p
	and (coalesce(cd_local_estoque_p,0) 	= 0 or b.cd_local_estoque = cd_local_estoque_p)
	and	b.dt_mesano_referencia 	<= dt_mesano_referencia_w
	and	a.nr_sequencia 		= nr_seq_lote_p;
	end;
end if;

return	qt_saldo_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
