CREATE OR REPLACE FUNCTION obter_saldo_lote_protocolo ( nr_seq_lote_prot_p bigint) RETURNS bigint AS $body$
DECLARE

 
vl_lote_protocolo_w			double precision;
vl_total_nota_w			double precision;
vl_saldo_w			double precision;


BEGIN 
vl_saldo_w := 0;
 
select	coalesce(obter_total_lote_protocolo(nr_seq_lote_prot_p),0) 
into STRICT	vl_lote_protocolo_w
;
 
select	coalesce(sum(vl_mercadoria),0) 
into STRICT	vl_total_nota_w 
from	nota_fiscal 
where	ie_situacao = '1' 
and	nr_seq_lote_prot = nr_seq_lote_prot_p;
 
vl_saldo_w := vl_lote_protocolo_w - vl_total_nota_w;
 
 
return	vl_saldo_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
