CREATE OR REPLACE FUNCTION obter_saldo_movto_bco_pend ( nr_seq_movto_pend_p bigint, dt_referencia_p timestamp) RETURNS bigint AS $body$
DECLARE


vl_credito_w	double precision;
vl_baixa_w	double precision;
vl_saldo_w	double precision;


BEGIN

select	coalesce(max(a.vl_credito),0)
into STRICT	vl_credito_w
from	movto_banco_pend a
where	a.nr_sequencia	= nr_seq_movto_pend_p;

select	coalesce(sum(a.vl_baixa),0)
into STRICT	vl_baixa_w
from	movto_banco_pend_baixa a
where	a.nr_seq_movto_pend	= nr_seq_movto_pend_p
and	a.dt_baixa		<= fim_dia(dt_referencia_p);

vl_saldo_w	:= coalesce(vl_credito_w,0) - coalesce(vl_baixa_w,0);

return vl_saldo_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
