CREATE OR REPLACE FUNCTION obter_saldo_movto_trans_banco ( nr_seq_lote_p bigint, nr_seq_banco_p bigint, nr_seq_saldo_p bigint, ie_tipo_saldo_p text) RETURNS bigint AS $body$
DECLARE


/*   Tipos de Saldo
	'C' - Credita
	'D' - Debita
	'S' - Saldo diferença
*/
vl_entrada_w				double precision := 0;
vl_saida_w				double precision := 0;
vl_diferenca_w 				double precision := 0;
vl_retorno_w				double precision := 0;


BEGIN

/* Calcula Entradas e Saídas */

select		sum(CASE WHEN b.ie_banco='C' THEN  a.vl_transacao  ELSE 0 END ),
		sum(CASE WHEN b.ie_banco='D' THEN  a.vl_transacao  ELSE 0 END )
into STRICT		vl_entrada_w,
		vl_saida_w
from		transacao_financeira b,
		movto_trans_financ a
where		a.nr_seq_trans_financ	= b.nr_sequencia
and		b.ie_banco		<> 'N'
and		a.nr_seq_banco		= nr_seq_banco_p
and		a.nr_seq_lote		= nr_seq_lote_p
and		a.nr_seq_saldo_banco	= nr_seq_saldo_p;


vl_diferenca_w	:= (coalesce(vl_entrada_w,0) - coalesce(vl_saida_w,0));

if (ie_tipo_saldo_p	= 'C') then
		vl_retorno_w	:= coalesce(vl_entrada_w,0);
elsif (ie_tipo_saldo_p	= 'D') then
		vl_retorno_w	:= coalesce(vl_saida_w,0);
elsif (ie_tipo_saldo_p	= 'S') then
		vl_retorno_w	:= coalesce(vl_diferenca_w,0);
end if;

return vl_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
