SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_saldo_movto_trans_financ ( nr_seq_lote_p bigint, nr_seq_caixa_p bigint, ie_tipo_saldo_p text) RETURNS bigint AS $body$
DECLARE


/*   Tipos de Saldo
	'C' - Credita
	'S' - Saídas não monetárias
	'M' - Saldo monetário
	'D' - Diferenca

*/
vl_entrada_w				double precision := 0;
vl_saida_w				double precision := 0;
vl_saldo_monetario_w			double precision := 0;
vl_diferenca_w 				double precision := 0;
vl_retorno_w				double precision := 0;
ie_saldo_caixa_w			varchar(01);



BEGIN

/* Calcula Entradas e Saídas */

select		sum(CASE WHEN b.ie_saldo_caixa='E' THEN  a.vl_transacao  ELSE 0 END ),
		sum(CASE WHEN b.ie_saldo_caixa='S' THEN  a.vl_transacao  ELSE 0 END )
into STRICT		vl_entrada_w,
		vl_saida_w
from		transacao_financeira b,
		movto_trans_financ a
where		a.nr_seq_trans_financ	= b.nr_sequencia
and		b.ie_caixa			in ('D', 'A')
and		a.nr_seq_caixa		= nr_seq_caixa_p
and		a.nr_seq_lote			= nr_seq_lote_p;

/* Calcula Saldo Monetário */

select		sum(CASE WHEN b.ie_saldo_caixa='E' THEN  a.vl_transacao WHEN b.ie_saldo_caixa='S' THEN (a.vl_transacao * -1)  ELSE 0 END )
into STRICT		vl_saldo_monetario_w
from		transacao_financeira b,
		movto_trans_financ a
where		a.nr_seq_trans_financ	= b.nr_sequencia
and		a.nr_seq_caixa		= nr_seq_caixa_p
and		b.ie_caixa			in ('M', 'A')
and		a.nr_seq_lote			= nr_seq_lote_p;


vl_diferenca_w	:= (coalesce(vl_saldo_monetario_w,0) - (coalesce(vl_entrada_w,0) - coalesce(vl_saida_w,0)));

if (ie_tipo_saldo_p	= 'E') then
		vl_retorno_w	:= coalesce(vl_entrada_w,0);
elsif (ie_tipo_saldo_p	= 'S') then
		vl_retorno_w	:= coalesce(vl_saida_w,0);
elsif (ie_tipo_saldo_p	= 'M') then
		vl_retorno_w	:= coalesce(vl_saldo_monetario_w,0);
elsif (ie_tipo_saldo_p	= 'D') then
		vl_retorno_w	:= coalesce(vl_diferenca_w,0);
end if;

return vl_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
