SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_saldo_nota_credito ( nr_seq_nota_credito_p bigint, dt_referencia_p timestamp) RETURNS bigint AS $body$
DECLARE

 
vl_nota_credito_w	double precision;
vl_saldo_w		double precision;
vl_baixa_w		double precision;
vl_titulos_w		double precision;
vl_nc_tit_rec_w		double precision;
vl_bordero_nc_w		double precision;


BEGIN 
 
select	coalesce(max(a.vl_nota_credito),0) 
into STRICT	vl_nota_credito_w 
from	nota_credito a 
where	a.nr_sequencia	= nr_seq_nota_credito_p;
 
select	coalesce(sum(vl_baixa),0) 
into STRICT	vl_baixa_w 
from	nota_credito_baixa 
where	nr_seq_nota_credito	= nr_seq_nota_credito_p 
and	coalesce(nr_seq_tit_pagar_baixa::text, '') = '' 
and	trunc(coalesce(dt_baixa,dt_atualizacao),'dd')	<= trunc(dt_referencia_p,'dd');
 
select	coalesce(sum(a.vl_titulo),0)		/* títulos a pagar gerados a partir da nota de crédito */
 
into STRICT	vl_titulos_w 
from	titulo_pagar a 
where	a.nr_seq_nota_credito		= nr_seq_nota_credito_p 
and	a.ie_situacao			<> 'C' 
and	trunc(a.dt_emissao,'dd')	<= trunc(dt_referencia_p,'dd');
 
select	coalesce(sum(a.vl_nota_credito),0)	/* valor da nota de crédito que foi vinculado a um título a receber */
 
into STRICT	vl_nc_tit_rec_w 
from	titulo_receber_nc a 
where	a.nr_seq_nota_credito	= nr_seq_nota_credito_p 
and	coalesce(a.nr_seq_liq::text, '') = '' 
and	exists (SELECT	1 
	from	titulo_receber_liq x 
	where	x.nr_titulo			= a.nr_titulo_rec 
	and	trunc(x.dt_recebimento,'dd')	<= trunc(dt_referencia_p,'dd'));
 
select	coalesce(sum(a.vl_nota_credito),0)	/* valor da nota de crédito que foi vinculado a um borderô de notas de crédito */
 
into STRICT	vl_bordero_nc_w 
from	bordero_nc_nota_credito a 
where	a.nr_seq_nota_credito	= nr_seq_nota_credito_p 
and	exists (SELECT	1 
	from	titulo_pagar x 
	where	x.nr_seq_bordero_nc		= a.nr_seq_bordero 
	and	trunc(x.dt_liquidacao,'dd')	<= trunc(dt_referencia_p,'dd'));
 
vl_saldo_w	:= vl_nota_credito_w - vl_baixa_w - vl_titulos_w - vl_nc_tit_rec_w - vl_bordero_nc_w;
 
RETURN vl_saldo_w;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_saldo_nota_credito ( nr_seq_nota_credito_p bigint, dt_referencia_p timestamp) FROM PUBLIC;

