CREATE OR REPLACE FUNCTION obter_saldo_parcela_cartao (nr_seq_parcela_p bigint, dt_referencia_p timestamp) RETURNS bigint AS $body$
DECLARE


vl_liquido_w		double precision;
vl_baixas_w		double precision;
vl_saldo_w		double precision;
dt_referencia_w		timestamp;


BEGIN

if (dt_referencia_p IS NOT NULL AND dt_referencia_p::text <> '') then
	dt_referencia_w	:= trunc(dt_referencia_p,'dd') + 86399/86400;
end if;

select	vl_liquido
into STRICT	vl_liquido_w
from	movto_cartao_cr_parcela
where	nr_sequencia	= nr_seq_parcela_p;

if (dt_referencia_p IS NOT NULL AND dt_referencia_p::text <> '') then
	select	coalesce(sum(vl_baixa),0)
	into STRICT	vl_baixas_w
	from	movto_cartao_cr_baixa
	where	nr_seq_parcela		= nr_seq_parcela_p
	and	dt_baixa		<= fim_dia(dt_referencia_w);
else
	select	coalesce(sum(vl_baixa),0)
	into STRICT	vl_baixas_w
	from	movto_cartao_cr_baixa
	where	nr_seq_parcela		= nr_seq_parcela_p;
end if;


vl_saldo_w	:= vl_liquido_w - vl_baixas_w;

return	vl_saldo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_saldo_parcela_cartao (nr_seq_parcela_p bigint, dt_referencia_p timestamp) FROM PUBLIC;

