SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_saldo_prescr_atend (cd_material_p bigint, nr_prescricao_p bigint, nr_seq_item_p bigint) RETURNS varchar AS $body$
DECLARE


ie_saldo_material_w		varchar(01) := 'S';
qt_mat_conta_w			double precision := 0;
qt_prescrito_w			double precision := 0;
qt_prescr_atend_w		double precision := 0;
nr_atendimento_w		bigint;


BEGIN

select	max(nr_atendimento)
into STRICT	nr_atendimento_w
from	prescr_medica
where	nr_prescricao		= nr_prescricao_p;

select	sum(a.qt_material)
into STRICT	qt_mat_conta_w
from	material_atend_paciente a
where	nr_atendimento		= nr_atendimento_w
and	cd_material		= cd_material_p
and	(nr_prescricao IS NOT NULL AND nr_prescricao::text <> '');

select	sum(qt_material)
into STRICT	qt_prescrito_w
from	prescr_material
where 	nr_prescricao		= nr_prescricao_p
and	nr_sequencia		= nr_seq_item_p;

select	sum(a.qt_material)
into STRICT	qt_prescr_atend_w
from	prescr_medica b,
	prescr_material a
where 	a.nr_prescricao		= b.nr_prescricao
and	(a.dt_baixa IS NOT NULL AND a.dt_baixa::text <> '')
and	a.nr_prescricao		<> nr_prescricao_p;


if	((qt_mat_conta_w - qt_prescr_atend_w) > qt_prescrito_w) then
	ie_saldo_material_w	:= 'N';
end if;

return	ie_saldo_material_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
