CREATE OR REPLACE FUNCTION obter_saldo_proj_rec_item ( nr_seq_proj_rec_p bigint, cd_material_p bigint, nr_seq_item_p bigint default 0) RETURNS bigint AS $body$
DECLARE



qt_mat_solicitado_w		solic_compra_item.qt_material%type;
qt_mat_projeto_w		projeto_recurso_item.qt_material%type;
qt_saldo_w			projeto_recurso_item.qt_material%type;


BEGIN

select	coalesce(sum(a.qt_material),0)
into STRICT	qt_mat_projeto_w
from	projeto_recurso_item a
where	a.nr_seq_proj_rec = nr_seq_proj_rec_p
and	a.cd_material = cd_material_p
and		((coalesce(nr_seq_item_p,0) = 0) or (coalesce(nr_seq_item_p,0) = a.nr_sequencia));

select	coalesce(sum(a.qt_material),0)
into STRICT	qt_mat_solicitado_w
from	solic_compra_item a
where	a.nr_seq_proj_rec = nr_seq_proj_rec_p
and	a.cd_material = cd_material_p;

qt_saldo_w	:= qt_mat_projeto_w - qt_mat_solicitado_w;

return	qt_saldo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
