CREATE OR REPLACE FUNCTION obter_saldo_tit_pagar_desdob ( nr_titulo_p bigint, dt_referencia_p timestamp) RETURNS bigint AS $body$
DECLARE


dt_referencia_w			timestamp;
vl_titulo_w				double precision;
vl_tit_desdob_w			double precision;
vl_saldo_Data_w			double precision;
vl_Baixa_w				double precision;
vl_alteracao_w			double precision;
vl_adiantamento_w			double precision;
ie_situacao_w				varchar(10);
IE_TRIB_ATUALIZA_SALDO_w		varchar(5);
vl_tributo_w				double precision;



BEGIN

dt_referencia_w	:= Trunc(dt_referencia_p,'dd') + 86399/86400;

select		b.IE_TRIB_ATUALIZA_SALDO
into STRICT		IE_TRIB_ATUALIZA_SALDO_w
from		PARAMETROS_CONTAS_PAGAR b,
		titulo_pagar a
where		b.cd_estabelecimento	= a.cd_estabelecimento
and		a.nr_titulo		= nr_titulo_p;

begin
select		vl_titulo,
		ie_situacao
into STRICT		vl_titulo_w,
		ie_situacao_w
from		Titulo_Pagar
where		nr_titulo		= nr_titulo_p;
exception
    		when others then
		vl_titulo_w		:= 0;
end;

select 	coalesce(sum(dividir_sem_round(vl_baixa,coalesce(vl_cotacao_moeda,1))),0)
into STRICT	vl_baixa_w
from 	titulo_pagar_baixa
where	nr_titulo		= nr_titulo_p
and 	dt_baixa		<= dt_referencia_w;

select 	coalesce(sum(vl_adiantamento),0)
into STRICT	vl_adiantamento_w
from 	titulo_pagar_adiant
where	nr_titulo		= nr_titulo_p
and 	trunc(coalesce(dt_contabil, dt_atualizacao), 'dd')	<= dt_referencia_w;

select 	coalesce(sum(vl_alteracao - vl_anterior),0)
into STRICT	vl_Alteracao_w
from 	titulo_pagar_Alt_Valor
where	nr_titulo		= nr_titulo_p
and	dt_alteracao		<= dt_referencia_w
and	ie_situacao_w		<> 'D';

select	coalesce(sum(a.vl_imposto),0)
into STRICT	vl_tributo_w
from	titulo_pagar_imposto a,
	titulo_pagar b
where	b.nr_titulo			= nr_titulo_p
and	a.nr_titulo			= b.nr_titulo
and	coalesce(b.nr_repasse_terceiro::text, '') = ''
and	coalesce(b.nr_seq_nota_fiscal::text, '') = ''
and	a.ie_pago_prev		= 'V'
and	IE_TRIB_ATUALIZA_SALDO_w	= 'S';

if (ie_situacao_w = 'D') then
	select	coalesce(sum(vl_titulo),0)
	into STRICT	vl_tit_desdob_w
	from	titulo_pagar A
	where	nr_titulo_original	= nr_titulo_p
	and	trunc(coalesce(dt_contabil, dt_emissao), 'dd') <= dt_referencia_p
	AND	IE_TIPO_TITULO	<> '4';
end if;

vl_saldo_data_w	:= vl_titulo_w - vl_baixa_w + vl_alteracao_w - vl_adiantamento_w - vl_tributo_w - vl_tit_desdob_w;

RETURN vl_Saldo_Data_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_saldo_tit_pagar_desdob ( nr_titulo_p bigint, dt_referencia_p timestamp) FROM PUBLIC;

