CREATE OR REPLACE FUNCTION obter_saldo_tit_rec_desdob (nr_titulo_p bigint) RETURNS bigint AS $body$
DECLARE


vl_retorno_w	double precision := 0;


BEGIN

if (nr_titulo_p IS NOT NULL AND nr_titulo_p::text <> '') then

	select   CASE WHEN a.vl_saldo_titulo=0 THEN 0  ELSE a.vl_saldo_titulo - coalesce(sum(b.vl_titulo),0) END  vl_saldo
	into STRICT	 vl_retorno_w
	FROM titulo_receber a
LEFT OUTER JOIN titulo_receber_desdob b ON (a.nr_titulo = b.nr_titulo)
WHERE a.nr_titulo =  nr_titulo_p group by
	       a.nr_titulo,
	       a.cd_pessoa_fisica,
	       a.cd_cgc,
	       a.vl_titulo,
	       a.vl_saldo_titulo,
	       a.vl_saldo_titulo,
	       a.dt_liquidacao,
	       a.dt_pagamento_previsto;

end if;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_saldo_tit_rec_desdob (nr_titulo_p bigint) FROM PUBLIC;

