CREATE OR REPLACE FUNCTION obter_saldo_total_consig ( cd_estabelecimento_p bigint, cd_material_p bigint, cd_local_estoque_p bigint, dt_mesano_referencia_p timestamp default null, nr_seq_lote_p bigint default null) RETURNS bigint AS $body$
DECLARE


cd_estabelecimento_w			smallint;
cd_material_w				integer;
dt_mesano_referencia_w		timestamp;
qt_estoque_proprio				bigint;
qt_estoque_p				bigint;


BEGIN

cd_estabelecimento_w	:= cd_estabelecimento_p;
select 	coalesce(m.cd_material_estoque, m.cd_material)
into STRICT		cd_material_w
from 		material m
where 	m.cd_material	= cd_material_p;

begin
select	max(f.dt_mesano_referencia)
into STRICT	dt_mesano_referencia_w
from 	fornecedor_mat_consignado f
where	f.cd_estabelecimento		= cd_estabelecimento_w
  and	f.cd_local_estoque		= coalesce(cd_local_estoque_p,  f.cd_local_estoque)
  and 	f.cd_material			= cd_material_w;
exception	
	when others then
		dt_mesano_referencia_w	:= PKG_DATE_UTILS.start_of(clock_timestamp(), 'MONTH', 0);
end;

begin
Select 	sum(f.qt_estoque)
into STRICT	qt_estoque_p
from 	fornecedor_mat_consignado f
where  f.cd_estabelecimento		= cd_estabelecimento_w
  and 	f.cd_material			= cd_material_w
  and	f.cd_local_estoque		= coalesce(cd_local_estoque_p, f.cd_local_estoque)
  and 	f.dt_mesano_referencia		= dt_mesano_referencia_w;
exception
	    when others then
		 	qt_estoque_p		:= 0;
end;

begin
	qt_estoque_proprio := obter_saldo_estoque(cd_estabelecimento_p, cd_material_p, cd_local_estoque_p, PKG_DATE_UTILS.start_of(clock_timestamp(), 'month', 0), qt_estoque_proprio);
	qt_estoque_p := coalesce(qt_estoque_p,0) + qt_estoque_proprio;
end;
	
return qt_estoque_p;	
END;
$body$
