CREATE OR REPLACE FUNCTION obter_saldo_trib_parc_cartao (nr_seq_movto_cartao_p bigint, nr_seq_parcela_p bigint) RETURNS bigint AS $body$
DECLARE


vl_retorno_w			double precision;
vl_imposto_w			movto_cartao_cr_parcela.vl_imposto%type;
vl_imposto_baixado_w	movto_cartao_cr_parcela.vl_imposto%type;


BEGIN

if (nr_seq_movto_cartao_p IS NOT NULL AND nr_seq_movto_cartao_p::text <> '') and (nr_seq_parcela_p IS NOT NULL AND nr_seq_parcela_p::text <> '') then

	/*buscar o valor  tota baixado de imposto*/

	select	coalesce(sum(a.vl_imposto),0)
	into STRICT	vl_imposto_baixado_w
	from	movto_cartao_cr_baixa a
	where	a.nr_seq_movto		= nr_seq_movto_cartao_p
	and		a.nr_seq_parcela	= nr_seq_parcela_p;

	select	coalesce(max(a.vl_imposto),0)
	into STRICT	vl_imposto_w
	from	movto_cartao_cr_parcela a
	where	a.nr_sequencia	= nr_seq_parcela_p
	and		a.nr_seq_movto	= nr_seq_movto_cartao_p;

	vl_retorno_w := vl_imposto_w - vl_imposto_baixado_w;


end if;

return	coalesce(vl_retorno_w,0);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_saldo_trib_parc_cartao (nr_seq_movto_cartao_p bigint, nr_seq_parcela_p bigint) FROM PUBLIC;

