CREATE OR REPLACE FUNCTION obter_saldo_trib_tit_rec (nr_titulo_p bigint, dt_referencia_p timestamp) RETURNS bigint AS $body$
DECLARE

vl_titulo_w		bigint;
vl_tributo_w		double precision;
vl_baixa_titulo_w	double precision;
vl_saldo_tributo_w	double precision;

 

BEGIN 
 
select	vl_titulo 
into STRICT	vl_titulo_w 
from	titulo_receber 
where	nr_titulo	= nr_titulo_p;
 
select	coalesce(sum(coalesce(vl_recebido,0)),0) vl_baixa_titulo 
into STRICT	vl_baixa_titulo_w 
from	titulo_receber_liq 
where	trunc(dt_recebimento,'dd') <= trunc(dt_referencia_p,'dd') 
and	coalesce(ie_lib_caixa, 'S') = 'S' 
and	nr_titulo	= nr_titulo_p;
 
select	coalesce(sum(coalesce(vl_tributo,0)),0) 
into STRICT	vl_tributo_w 
from	titulo_receber_trib 
where	nr_titulo	= nr_titulo_p;
 
vl_saldo_tributo_w := vl_tributo_w - (dividir_sem_round(vl_baixa_titulo_w,vl_titulo_w) * vl_tributo_w);
 
return	coalesce(vl_saldo_tributo_w,0);
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
