CREATE OR REPLACE FUNCTION obter_san_prestador_exec (nr_sequencia_p bigint, ie_responsavel_credito_p text, cd_convenio_p bigint) RETURNS varchar AS $body$
DECLARE

 
cd_cgc_prestador_w	varchar(14);
ie_entra_conta_w	varchar(1):= 'S';
cd_estabelecimento_w	smallint;
ds_retorno_w		varchar(12):= '';


BEGIN 
 
begin 
select 	max(coalesce(ie_entra_conta,'S')) 
into STRICT	ie_entra_conta_w 
from 	regra_honorario 
where 	cd_regra = ie_responsavel_credito_p;
exception 
	when others then 
	ie_entra_conta_w:= 'S';
end;
 
begin 
select 	max(coalesce(cd_cgc_prestador,'0')), 
	max(obter_estab_atend(nr_atendimento)) 
into STRICT	cd_cgc_prestador_w, 
	cd_estabelecimento_w 
from 	procedimento_paciente 
where	nr_sequencia = nr_sequencia_p;
exception 
	when others then 
	cd_cgc_prestador_w:= '0';
end;
 
if (cd_cgc_prestador_w = '0') or (ie_entra_conta_w = 'S') then	 
	select 	max(substr(obter_valor_conv_estab(cd_convenio_p,cd_estabelecimento_w,'CD_INTERNO'),1,12)) 
	into STRICT	ds_retorno_w 
	;
else 
	select 	max(substr(obter_prestador_convenio(cd_cgc_prestador_w,cd_convenio_p,null),1,12)) 
	into STRICT	ds_retorno_w 
	;	
end if;
 
return ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
