CREATE OR REPLACE FUNCTION obter_san_qtd_total_reposicoes (nr_seq_transfusao_p bigint, nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(50);

qt_reposicao_w		bigint;
qt_solicitacao_w	bigint;


BEGIN
select	   count(*)
into STRICT	   qt_reposicao_w
from	   san_doacao a,
	   san_transfusao b
where	   a.cd_pessoa_dest = b.cd_pessoa_fisica
and	   b.nr_sequencia = nr_seq_transfusao_p;

select	   count(*)*2
into STRICT	   qt_solicitacao_w
from	   san_producao a,
	   san_transfusao b
where	   a.nr_seq_transfusao = b.nr_sequencia
and	   b.nr_atendimento = nr_atendimento_p;

ds_retorno_w := ( coalesce(qt_reposicao_w,0) - coalesce(qt_solicitacao_w,0) );

if (ds_retorno_w < 0) then
ds_retorno_w := ( '('|| ds_retorno_w ||') ' || wheb_mensagem_pck.get_texto(802876));
else
ds_retorno_w := ( '('|| ds_retorno_w ||') ' || wheb_mensagem_pck.get_texto(802877));
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
