SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_san_tipo_doador (cd_pessoa_fisica_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


qt_doacoes_w		smallint;
nr_idade_w		smallint;
ds_retorno_w		varchar(20);


/*
ie_opcao_p
  T - Tipo de doador (1ª vez ou repetição)
  I - Faixa de idade (18 a 29 anos ou acima de 29 anos)

ds_retorno_W
  E - Entre 18 a 29 anos
  A - Acima de 29
*/
BEGIN

if (ie_opcao_p = 'T') then
	select	count(*)
	into STRICT	qt_doacoes_w
	from	san_doacao b,
		pessoa_fisica a
	where	a.cd_pessoa_fisica   = b.cd_pessoa_fisica
	and	a.cd_pessoa_fisica   = cd_pessoa_fisica_p;

	if qt_doacoes_w > 1 then
		ds_retorno_w := 'R';
	else
		ds_retorno_w := 'P';
	end if;
elsif (ie_opcao_p = 'I') then
	select	substr(obter_idade(a.dt_nascimento,clock_timestamp(),'A'),1,60)
	into STRICT	nr_idade_w
	from	pessoa_fisica a
	where	a.cd_pessoa_fisica = cd_pessoa_fisica_p;

	if (nr_idade_w >= 18) and (nr_idade_w <= 29) then
		ds_retorno_w := 'E';
	elsif (nr_idade_w > 29) then
		ds_retorno_w := 'A';
	end if;
end if;

return ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
