CREATE OR REPLACE FUNCTION obter_score_cardiovas_ep_md (ie_cardiovascular_p bigint ) RETURNS bigint AS $body$
DECLARE


   qt_pt_cardiovascular_w	smallint := 0;

BEGIN
	--- Inicio MD13
	-- cardiovascular
	if (ie_cardiovascular_p IS NOT NULL AND ie_cardiovascular_p::text <> '') then
		if (ie_cardiovascular_p	=	1) then
