		elsif (ie_cardiovascular_p	in (6,8)) then
			qt_pt_cardiovascular_w	:=	8;
		end if;
	end if;
	--- Fim MD13
    RETURN coalesce(qt_pt_cardiovascular_w,0);
   
END;
