CREATE OR REPLACE FUNCTION obter_score_escala_candida (qt_score_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);


BEGIN

if (qt_score_p >= 2.5) then
	ds_retorno_w	:= 	'Sugestivo benefício para tratamento anti-fúngico precoce';
else
	ds_retorno_w	:= 	'Sem evidência de benefício para tratamento anti-fúngico precoce';
end if;


return	ds_retorno_w;

end;
