CREATE OR REPLACE FUNCTION obter_score_escala_cpis_md ( ie_secrecao_purulenta_p text, ie_temperatura_p bigint, ie_leucocitose_p bigint, ie_secrecao_traqueal_p bigint, ie_pao2_p bigint, ie_rx_torax_p bigint ) RETURNS bigint AS $body$
DECLARE

  ie_secrecao_purulenta_w smallint:= 0;

BEGIN
  if (ie_secrecao_purulenta_p = 'S') then
