CREATE OR REPLACE FUNCTION obter_score_escala_downton_md ( ie_sedative_p text, ie_diruetics_p text, ie_other_diruetics_p text, ie_antiparkinsonian_p text, ie_antidepressants_p text, ie_visual_impair_p text, ie_hearing_impair_p text, ie_limb_impair_p text, ie_gait_p bigint, ie_previous_falls_p bigint, ie_mental_state_p bigint ) RETURNS bigint AS $body$
DECLARE

  qt_pontuacao_w bigint;

BEGIN
  qt_pontuacao_w := 0;

  if (ie_sedative_p = 'S') then
    qt_pontuacao_w	:= qt_pontuacao_w +1;
  end if;

  if (ie_diruetics_p = 'S') then
    qt_pontuacao_w	:= qt_pontuacao_w +1;
  end if;

  if (ie_other_diruetics_p = 'S') then
    qt_pontuacao_w	:= qt_pontuacao_w +1;
  end if;

  if (ie_antiparkinsonian_p = 'S') then
    qt_pontuacao_w	:= qt_pontuacao_w +1;
  end if;

  if (ie_antidepressants_p = 'S') then
    qt_pontuacao_w	:= qt_pontuacao_w +1;
  end if;

  if (ie_visual_impair_p = 'S') then
    qt_pontuacao_w	:= qt_pontuacao_w +1;
  end if;

  if (ie_hearing_impair_p = 'S') then
    qt_pontuacao_w	:= qt_pontuacao_w +1;
  end if;

  if (ie_limb_impair_p = 'S') then
    qt_pontuacao_w	:= qt_pontuacao_w +1;
  end if;

  if (ie_gait_p = 2) then
    qt_pontuacao_w	:= qt_pontuacao_w +1;
  end if;

  return qt_pontuacao_w + coalesce(ie_previous_falls_p, 0) + coalesce(ie_mental_state_p, 0);
end;
$body$
