CREATE OR REPLACE FUNCTION obter_score_escala_nrs_md ( qt_estado_nutricional_p bigint, qt_gravidade_doenca_p bigint, qt_idade_p bigint ) RETURNS bigint AS $body$
DECLARE

  qt_total_w bigint := 0;

BEGIN
  qt_total_w := qt_total_w + coalesce(qt_estado_nutricional_p, 0) + coalesce(qt_gravidade_doenca_p, 0);
  if (qt_idade_p >= 70) then
	qt_total_w := qt_total_w + 1;
  end if;
  return qt_total_w;
end;
