CREATE OR REPLACE FUNCTION obter_score_freq_card_ep_md (qt_fc_p bigint ) RETURNS bigint AS $body$
DECLARE


   qt_pt_fc_w	smallint := 0;

BEGIN
	--- Inicio MD4
	-- frequencia cardiaca
	if (qt_fc_p IS NOT NULL AND qt_fc_p::text <> '') then
		if (qt_fc_p	<=	39) then
			qt_pt_fc_w	:=	8;
		elsif (qt_fc_p	<=	49) then
			qt_pt_fc_w	:=	2;
		elsif (qt_fc_p	<=	80) then
			qt_pt_fc_w	:=	1;
		elsif (qt_fc_p	<=	100) then
			qt_pt_fc_w	:=	2;
		elsif (qt_fc_p	<=	120) then
			qt_pt_fc_w	:=	4;
		else
			qt_pt_fc_w	:=	8;
		end if;
	end if;
	--- Fim MD4
    RETURN coalesce(qt_pt_fc_w,0);
   
END;
$body$
