CREATE OR REPLACE FUNCTION obter_score_glob_bran_ep_md (qt_glob_brancos_p bigint ) RETURNS bigint AS $body$
DECLARE


   qt_pt_glob_brancos_w	smallint := 0;

BEGIN
	--- Inicio MD6
	-- globulos brancos
	if (qt_glob_brancos_p IS NOT NULL AND qt_glob_brancos_p::text <> '') then
		if (qt_glob_brancos_p	<=	3000) then
			qt_pt_glob_brancos_w	:=	4;
		elsif (qt_glob_brancos_p	<=	3999) then
			qt_pt_glob_brancos_w	:=	2;
		elsif (qt_glob_brancos_p	<=	10000) then
			qt_pt_glob_brancos_w	:=	1;
		elsif (qt_glob_brancos_p	<=	20000) then
			qt_pt_glob_brancos_w	:=	2;
		else
			qt_pt_glob_brancos_w	:=	4;
		end if;
	end if;
	--- Fim MD6	
    RETURN coalesce(qt_pt_glob_brancos_w,0);
   
END;
